import React, { Component } from 'react';
import profile from './imgs/profile.jpg';
import tiletunes from './imgs/tiletunes.png';
import discobot from './imgs/discobot.png';
import pvdcm from './imgs/pvdcm.png';
import wicshistory from './imgs/wicshistory.png';
import resume from './resume.pdf';
import wicshistorycomputer from './imgs/wicshistory-computer.png';
import wicshistorynotebook1 from './imgs/wicshistory-notebook1.jpeg';
import wicshistorynotebook2 from './imgs/wicshistory-notebook2.jpeg';
import wicshistorynotebook3 from './imgs/wicshistory-notebook3.jpeg';
import wicshistoryadobexd from './imgs/wicshistory-adobexd.jpg';
import wicshistorywebsite from './imgs/wicshistory-website.jpg';

class Body extends Component {
  constructor(props) {
    super(props);

    this.state = {
      page: this.props.page[0].index
    }
  }

  updateAboutPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[0].index
    });
  }

  updatePortfolioPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[1].index
    });
  }

  updateTileTunesPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[2].index
    });
  }

  updateDiscoBotPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[3].index
    });
  }

  updatePVDCMPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[4].index
    });
  }

  updateWiCSHistoryPage = event => {
    event.preventDefault();

    this.setState({
      page: this.props.page[5].index
    });
  }

  renderHeader() {
    return(
      <header className="App-header">
        <div>
          <button type="button" onClick={this.updateAboutPage}
          className="App-header-name">
            meredith young-ng
          </button>
        </div>
        <div className="App-header-pages">
          <button type="button" onClick={this.updateAboutPage}>
            <span id="headerActive">about</span>
          </button>
          <button type="button" onClick={this.updatePortfolioPage}>
            <span id="headerActive">work</span>
          </button>
        </div>
      </header>
    );
  }

  renderAbout() {
    return(
      <div className="App-body">
        <div className="App-body-bio">
          <h1>hi, i'm meredith.</h1>
          <h2>engineer • designer • writer • creator.</h2>
          <div className="App-body-bio-text">
            <p>I’m a M.S. student at Brown University, studying computer
            science. Currently, I work on smartphone augmented reality research
            with Jeff Huang. Previously, I worked on simulating a 3D crocheting
            machine and other textiles in the Cornell Graphics and Vision Lab
            with Steve Marschner and François Guimbretière. I’m excited by
            interdisciplinary research, particularly at the intersection of
            computer graphics and HCI.</p>
            <p className="App-body-bio-hobbies">When I’m not in the lab,
            you can find me practicing violin in Steinert, working on
            engineering-related diversity outreach, or reading and writing
            personal essays.</p>
          </div>
          <div className="App-body-bio-buttons">
            <a className="Button-work" href="/#"
            onClick={this.updatePortfolioPage}>work</a>
            <a href={resume} target="_blank" rel="noopener noreferrer"
            className="Button-work">
              resume
            </a>
          </div>
        </div>
        <div className="App-body-pic">
          <img src={profile} alt="Profile"/>
          <div className="App-body-pic-info">
            <p><span id="App-body-status">currently</span>: brown m.s. ‘21</p>
            <p><span id="App-body-status">previously</span>: cornell b.s. ’19</p>
            <p><span id="App-body-status">future</span>: research intern @ ge
            global research, summer '20</p>
          </div>
        </div>
      </div>
    );
  }

  renderTileTunes() {
    return(
      <div className="App-body">
        <h2>Tile Tunes</h2>
      </div>
    );
  }

  renderDiscoBot() {
    return(
      <div className="App-body">
        <h2>Disco Bot</h2>
      </div>
    );
  }

  renderPVDCM() {
    return(
      <div className="App-body">
        <h2>Providence Children's Museum Redesign</h2>
      </div>
    );
  }

  renderWiCSHistory() {
    return(
      <div className="App-body-project">
        <h2>History of Women in Computer Science</h2>
        <a href="https://wics-history.herokuapp.com" target="_blank"
        rel="noopener noreferrer" className="App-body-wics">
          https://wics-history.herokuapp.com
        </a>
        <h3>Nov. 2019 — Present</h3>
        <img src={wicshistorycomputer} alt="WiCS History Website"
        className="App-body-project-main-pic"/>
        <h4>Introduction — Bridging the Gender Gap in Computer Science</h4>
        <p>Studies show that girls lose interest in STEM by middle school. By
          college, women only earn 18% of all computer science degrees, and
          &#60; 20&#37; of people in tech are women. While women have
          continuously made (and continue to make) influential contributions to
          all fields of computer science, the gender gap in computer science
          persists. My webpage aims to provide an interactive experience for
          people to learn more about the depth of the history of women in
          computer science, and to provide resources for people to start
          learning how to code. It only takes one person—YOU—to start helping
          me bridge the gender gap in computer science.
        </p>
        <h4>Overview</h4>
        <p>My design process for this project was as follows:<br />
          1.	Research<br />
          2.	Low-Fidelity Prototypes (Sketches) & User Testing <br />
          3.	High-Fidelity Prototypes: Adobe XD & Webpage (React)<br />
          4.	Reflection & Future Work<br /><br />
          Throughout all of my diversity in engineering outreach, I’ve always
          wondered how I could help bridge the gender gap on a more global
          scale. As I progressed in my engineering career, I’ve always been
          presented with the same key historical women in computing (e.g.,
          Grace Hopper)—I wanted to bring more attention to some of the
          lesser-known women in computing, and highlight their achievements.
          This manifested as my Bridging the Gender Gap in Computer Science
          webpage. Along the way, I learned Adobe XD, React, and how to host
          an app on Heroku.
        </p>
        <h4>Research</h4>
        <p>Before I began prototyping, I decided to see how other webpages
          represent the history of women in computer science and engineering.
          Wikipedia has its Women in computing page and a comprehensive
          timeline of women in computing; however, these lists don’t have
          pictures humanizing the women in addition to their academic
          achievements. The significance of these academic achievements is also
          not clear to someone without a background in computing. Other
          websites such as EngineerGirl and League of Coders primarily focus on
          women engineers in the US; further biographical information such as
          their field and lifetime aren’t listed next to their names and
          pictures.
        </p>
        <h4>Low-Fidelity Prototypes & User Testing</h4>
        <p>Before I began prototyping, I decided to see how other webpages
          represent the history of women in computer science and engineering.
          Wikipedia has its Women in computing page and a comprehensive timeline
          of women in computing; however, these lists don’t have pictures
          humanizing the women in addition to their academic achievements. The
          significance of these academic achievements is also not clear to
          someone without a background in computing. Other websites such as
          EngineerGirl and League of Coders primarily focus on women engineers
          in the US; further biographical information such as their field and
          lifetime aren’t listed next to their names and pictures. <br /><br />
          Based on my research analysis, I started sketching prototypes, as
          shown below.
        </p>
        <div className="App-body-project-notebook">
          <img src={wicshistorynotebook1} alt="Sketches"/>
          <img src={wicshistorynotebook2} alt="Sketches"/>
        </div>
        <p>Before sketching my next prototype, I conducted some simple Wizard
          of Oz testing using convenience sampling.
        </p>
        <div className="App-body-project-notebook">
          <img src={wicshistorynotebook3} alt="Low-Fidelity Prototype"/>
        </div>
        <h4>High-Fidelity Prototypes</h4>
        <p>After sketching my webpage design, I decided to make a high-fidelity
          prototype in AdobeXD as shown below. This enabled me to better
          visualize my webpage before I began coding.
        </p>
        <div className="App-body-project-webpage">
          <img src={wicshistoryadobexd} alt="High-Fidelity Prototype"/>
        </div>
        <p>I coded my final prototype using React. My webpage has a cover image
          that attracts attention to the statistics regarding this issue, and
          cuts off after the “History” header to indicate that there is more
          information below. The navbar also provides affordances to show other
          sections of the page (History and Learn). Arrows indicate that the
          filtering dropdown buttons (Field and Country) expand to show more
          options. In addition, the dropdown buttons render whatever Field and
          Country is selected, once the user selects an option. I decided to use
          green and blue in my color theme, as the “calmer” colors in the red,
          green, and blue rendering trio. The blue in the color theme is used to
          accentuate potential user actions, particularly for buttons and
          keywords.<br /><br />
          Data is passed through props to <span id="codeFont">FilteredList
          </span>, <span id="codeFont">CardYou</span>, and <span id="codeFont">
          CodingExp</span>. The <span id="codeFont">FilteredList</span>&nbsp;
          component filters and sorts through the database of historical women
          computer scientists, with the <span id="codeFont">List</span>
          &#32;component rendering the <span id="codeFont">Card</span>&#32; of each woman
          as applicable. Filters (field and country) and sorting (by date of
          birth) can be achieved by clicking the appropriate filter from the
          dropdown and the sorting checkbox, respectively.<br /><br />
          <span id="codeFont">CardYou</span>&#32; uses the default “You” information
          through props default values for the user inputs in &#32;
          <span id="codeFont">state</span>&#32;. Clicking the “Join Us” button
          renders whatever information the user has entered in the text entry
          form (name, birth year, field, and country) on the “You” card by
          updating&nbsp;<span id="codeFont">state</span>&nbsp;and the “You”&nbsp;
          <span id="codeFont">Card</span>&nbsp;component.<br /><br />

CodingExp uses the selected radio button option to recommend coding resources to the user based on their coding experience when the user clicks the “Let’s Get Started” button. The coding experience options for the radio buttons and labels are referenced through this.props.choices,  and the currently selected radio button id is stored in state as checkedRadio. This is updated every time the user selects a different option. The resources shown (rendered) change each time the user clicks the “Let’s Get Started” button via updating the value of resourceOption in state.

My final prototype is currently hosted on Heroku. The code can be found on my GitHub. View the project here.

        </p>
      </div>
    );
  }

  renderPortfolio() {
    return(
      <div className="App-body-portfolio">
        <h2>my design philosophy: ideate, research, <i>iterate!</i></h2>
        <div className="App-body-portfolio-items">
          <div>
            <input type="image" src={tiletunes} alt="Tile Tunes"
            onClick={this.updateTileTunesPage}/>
            <h3>Tile Tunes</h3>
            <p>HCI, UI/UX, Music</p>
          </div>
          <div>
            <input type="image" src={discobot} alt="Disco Bot"
            onClick={this.updateDiscoBotPage}/>
            <h3>Disco Bot</h3>
            <p>HRI, UI/UX, Music</p>
          </div>
          <div>
            <input type="image" src={pvdcm} alt="Providence Children's Museum
            Redesign" onClick={this.updatePVDCMPage}/>
            <h3>Providence Children's Museum Redesign</h3>
            <p>UI/UX</p>
          </div>
          <div>
            <input type="image" src={wicshistory} alt="History of Women in
            Computer Science" onClick={this.updateWiCSHistoryPage}/>
            <h3>History of Women in Computer Science</h3>
            <p>UI/UX, Outreach</p>
          </div>
        </div>
      </div>
    );
  }

  renderPageBody() {
    if (this.state.page === 0) {
      return(
        <div>{this.renderAbout()}</div>
      );
    } else if (this.state.page === 1) {
      return(
        <div>{this.renderPortfolio()}</div>
      );
    } else if (this.state.page === 2) {
      return(
        <div>{this.renderTileTunes()}</div>
      );
    } else if (this.state.page === 3) {
      return(
        <div>{this.renderDiscoBot()}</div>
      );
    } else if (this.state.page === 4) {
      return(
        <div>{this.renderPVDCM()}</div>
      );
    } else if (this.state.page === 5) {
      return(
        <div>{this.renderWiCSHistory()}</div>
      );
    } else {
      return(
        <div className="App-body">
          <p>Under construction...</p>
        </div>
      );
    }
  }

  render() {
    return (
      <div>
        {this.renderHeader()}
        {this.renderPageBody()}
      </div>
    );
  }
}

export default Body;
